const heroSlider = new Swiper('.hero-slider__container', {
	// autoplay: {
	//   delay: 2500,
	//   disableOnInteraction: false,
	// },
	effect: 'fade',
	pagination: {
		el: '.hero-slider__pagination',
		clickable: true,
	},
});

$(window).scroll(function () {
	if ($(this).scrollTop() > 1) {
		$('.header').addClass('header--sticky');
	} else {
		$('.header').removeClass('header--sticky');
	}
});