"use strict";

svg4everybody();
new Swiper('.hero-slider__container', {
  autoplay: {
    delay: 2500,
    disableOnInteraction: false
  },
  effect: 'fade',
  pagination: {
    el: '.hero-slider__pagination',
    clickable: true
  }
}); // $('.sandwich__open-btn--nav').click(function () {
//   $('.main-nav').toggleClass('main-nav--opened');
// });

$('.sandwich__open-btn').click(function () {
  $(this).toggleClass('sandwich__open-btn--opened');
});

if (window.innerWidth < 680) {
  $('.sandwich__open-btn').addClass('sandwich__open-btn--mobile').removeClass('sandwich__open-btn--nav');
}

$('.header .sandwich__open-btn--mobile.sandwich__open-btn--main').click(function () {
  $('.header .mobile-menu--main').toggleClass('mobile-menu--opened');
  $('body').toggleClass('overflow');
});
$('.header .sandwich__open-btn--mobile.sandwich__open-btn--fixed').click(function () {
  $('.header.header--sticky .mobile-menu--fixed').toggleClass('mobile-menu--opened');
  $('body').toggleClass('overflow');
}); // Открытие второго уровня меню

$('.mobile-nav__item').click(function () {
  $(this).children('.mobile-submenu').addClass('mobile-submenu--opened');
  return false;
});
$('.mobile-menu__close').click(function () {
  $(this).closest('.mobile-submenu').removeClass('mobile-submenu--opened');
  return false;
}); // Создание липкой шапки при скролле (клон основной)

var header = $('.header'),
    cloneHeader = header.clone();
cloneHeader.addClass('header--sticky').removeAttr('id'); // Добавление классов кнопки и меню для различия от основной

cloneHeader.find('.sandwich__open-btn').addClass('sandwich__open-btn--fixed').removeClass('sandwich__open-btn--main');
cloneHeader.find('.mobile-menu').addClass('mobile-menu--fixed').removeClass('mobile-menu--main');
header.before(cloneHeader); // Появление липкой шапки при скролле

$(window).scroll(function () {
  if ($(window).scrollTop() > 150) {
    cloneHeader.addClass('header--is-show');
  } else {
    cloneHeader.removeClass('header--is-show');
  }
});
$('.sidebar-subnav').hide();
$('.sidebar-nav__sign').click(function () {
  $(this).siblings('.sidebar-subnav').slideToggle(200);
  $(this).closest('.sidebar-nav__item').toggleClass('sidebar-nav__item--opened');
});

if (window.innerWidth < 1200) {
  $('.sidebar-nav__list').hide();
}

$('.sidebar-nav__btn').click(function () {
  $(this).siblings('.sidebar-nav__list').slideToggle(200);
  $(this).toggleClass('sidebar-nav__btn--opened');
});
$(window).scroll(function () {
  var scrollElement = $('.scrolltop');

  if ($(window).scrollTop() > 700) {
    scrollElement.addClass('scrolltop--show');
  } else {
    scrollElement.removeClass('scrolltop--show');
  }
});
$('a[href*="#"]').on('click', function (e) {
  e.preventDefault();
  $('html, body').animate({
    scrollTop: $($(this).attr('href')).offset().top
  }, 500, 'linear');
});

if (!Number.isNan) {
  Object.defineProperty(Number, 'isNaN', {
    value: function value(_value) {
      return _value !== _value;
    }
  });
}